// Home.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './Home.module.css';
import Uncover from './Uncover';

const Home = () => {
  const [isContentVisible, setIsContentVisible] = useState(false);

  const handleUncoverClick = () => {
    setIsContentVisible(true);
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1 className={styles.title}>Dream Journal</h1>
      </header>
      <main className={styles.main}>
        <section className={styles.purpose}>
          <h2 className={styles.purposeTitle}>Welcome to Your Dream Journal</h2>
          <p className={styles.description}>
            Write down your dreams and embark on a fantastical journey. Capture your dreams in
            detail, analyze them, and discover hidden meanings.
          </p>
        </section>
        <Uncover isContentVisible={isContentVisible} handleUncoverClick={handleUncoverClick} />
        {!isContentVisible && (
          <Link to="/login" className={styles.loginButton}>
            Get Started
          </Link>
        )}
      </main>
      <footer className={styles.footer}>
        <p>&copy; {new Date().getFullYear()} Dream Journal</p>
      </footer>
    </div>
  );
};

export default Home;
