import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './Login.module.css';
import { useLocation } from 'react-router-dom';
import { appUserLogin } from '../../services/dreams';

function Login() {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    const location = useLocation();
    const [displayedSuccessMessage, setDisplayedSuccessMessage] = useState(false);
    const successMessage = location.state?.successMessage;

    useEffect(() => {
        if (successMessage && !displayedSuccessMessage) {
            sessionStorage.setItem('successMessage', successMessage);
            setDisplayedSuccessMessage(true);
        }
    }, [successMessage, displayedSuccessMessage]);

    const handleLogin = async (e) => {
        console.log("Logging in with:", username, password);
        const login = {
            username,
            password
        };

        const result = await appUserLogin(login);

        if (result.success) {
            setUsername('');
            setPassword('');
            navigate("/dreamlogs", {
                state: { successMessage: "User registered successfully! You can now log in." }
            });
        } else {
            setErrorMessage(result.message);
        }
    };

    const handleSignup = () => {
        navigate("/signup");
    };

    return (
        <div className={classes.container}>
            <div className={classes.loginBox}>
            {successMessage && !displayedSuccessMessage && (
                    <p className={classes.successMessage}>{successMessage}</p>
                )}
                <h2 className={classes.title}>Login</h2>
                {errorMessage && <p className={classes.errorMsg}>{errorMessage}</p>}
                <input
                    type="text"
                    placeholder="Username"
                    className={classes.inputField}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    className={classes.inputField}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <div className={classes.buttons}>
                    <button className={classes.loginButton} onClick={handleLogin}>Login</button>
                    <button className={classes.signupButton} onClick={handleSignup}>Signup</button>
                </div>
            </div>
        </div>
    );
}

export default Login;
