import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './Login.module.css';
import { useLocation } from 'react-router-dom';

function Login() {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const location = useLocation();
    const successMessage = location.state?.successMessage;
    
    useEffect(() => {
        if (successMessage) {
            sessionStorage.setItem('successMessage', successMessage);
        }
    }, [successMessage]);

    useEffect(() => {
        if (sessionStorage.getItem('successMessage')) {
            navigate("/login", { state: { successMessage: null } });
            sessionStorage.removeItem('successMessage');
        }
    }, [navigate]);

    const handleLogin = () => {
        console.log("Logging in with:", username, password);
    };

    const handleSignup = () => {
        navigate("/signup");
    };

    return (
        <div className={classes.container}>
            <div className={classes.loginBox}>
            {successMessage && <p className={classes.successMessage}>{successMessage}</p>}
                <h2 className={classes.title}>Login</h2>
                <input
                    type="text"
                    placeholder="Username"
                    className={classes.inputField}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    className={classes.inputField}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <div className={classes.buttons}>
                    <button className={classes.loginButton} onClick={handleLogin}>Login</button>
                    <button className={classes.signupButton} onClick={handleSignup}>Signup</button>
                </div>
            </div>
        </div>
    );
}

export default Login;
