import React, { useState, useEffect } from 'react';
import classes from './DreamInfo.module.css';
import { useParams } from 'react-router-dom';

function DreamInfo() {
    const { logId } = useParams();
    const [dreamData, setDreamData] = useState(null);

    useEffect(() => {
        // Fetch the specific dream data by its ID.
        // This is a placeholder; replace it with an actual API call.
        fetch(`/api/dreams/${logId}`)
            .then(response => response.json())
            .then(data => setDreamData(data))
            .catch(error => console.error("Error fetching dream details:", error));
    }, [logId]);

    if (!dreamData) {
        return <div className={classes.loading}>Loading...</div>;
    }

    return (
        <div className={classes.container}>
            <div className={classes.dreamBox}>
                <h2 className={classes.title}>{dreamData.title}</h2>
                <p><strong>Date:</strong> {dreamData.date}</p>
                <p><strong>Tag:</strong> {dreamData.tag}</p>
                <p className={classes.description}>{dreamData.description}</p>
            </div>
        </div>
    );
}

export default DreamInfo;
